mapper.select:
select count(*)
from in_store t1, product t2
where t1.product_id = t2.product_id

    and t1.store_id = #{storeId}


    and t2.product_name like concat("%", #{productName}, "%")


    and t1.create_time >= #{startTime}


    and t1.create_time <= #{endTime}

select t1.*, t2.product_name, t2.in_price, t3.store_name, t4. user_code
from in_store t1, product t2, store t3, user_info t4
where t1.product_id = t2.product_id and t1.store_id = t3.store_id and t1.create_by = t4.user_id

    and t1.store_id = #{inStore.storeId}


    and t2.product_name like concat("%", #{inStore.productName}, "%")


    and t1.create_time >= #{inStore.startTime}


    and t1.create_time <= #{inStore.endTime}

order by t1.ins_id
limit #{page.limitIndex},#{page.pageSize}
